package java_Basic;

public class array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] ar=new int[3];
		ar[0]=2;
		ar[1]=4;
		ar[2]=6;
		for(int i=0;i<ar.length;i++) {
		System.out.print(" "+ar[i]);}
		System.out.print("\n");
		System.out.println(ar[1]);
		
		
		String [] wd=new String[]{ "isha","ram" ,"prachi" };
		System.out.println("\n"+wd[2]);

	}

}
